import axios from 'axios';
import React, {SyntheticEvent, useState} from 'react';
import {Redirect} from 'react-router-dom';
import GoogleLogin from 'react-google-login';
const googleClientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [redirect, setRedirect] = useState(false);

    const submit = async (e: SyntheticEvent) => {
        e.preventDefault();

        await axios.post('login', {
            email,
            password
        });

        setRedirect(true);
    }
    const responseGoogle = async (response:any) => {
      const data = JSON.stringify(response)

     // await axios.get('/oauth/auth/google');
      console.log(JSON.stringify(response.email));
    }


  const [gapi, setGapi] = useState();
  const [googleAuth, setGoogleAuth] = useState();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [name, setName] = useState('');

  const [imageUrl, setImageUrl] = useState();

  const onSuccess = (googleUser: { getBasicProfile: () => any; }) => {
    setIsLoggedIn(true);
    const profile = googleUser.getBasicProfile();
    setName(profile.getName());
    setEmail(profile.getEmail());
    setImageUrl(profile.getImageUrl());
  };

  const onFailure = () => {
    setIsLoggedIn(false);
  }



    if (redirect) {
        return <Redirect to={'/'}/>;
    }
  const renderSigninButton = (_gapi: { signin2: { render: (arg0: string, arg1: { scope: string; width: number; height: number; longtitle: boolean; theme: string; onsuccess: (googleUser: { getBasicProfile: () => any; }) => void; onfailure: () => void; }) => void; }; }) => {
    _gapi.signin2.render('google-signin', {
      'scope': 'profile email',
      'width': 240,
      'height': 50,
      'longtitle': true,
      'theme': 'dark',
      'onsuccess': onSuccess,
      'onfailure': onFailure
    });
  }
    return (
        <main className="form-signin">
            <form onSubmit={submit}>
                <h1 className="h3 mb-3 fw-normal">Please sign in</h1>

                <input type="email" className="form-control" placeholder="Email" required
                       onChange={e => setEmail(e.target.value)}
                />

                <input type="password" className="form-control" placeholder="Password" required
                       onChange={e => setPassword(e.target.value)}
                />

                <button className="w-100 btn btn-lg btn-primary" type="submit">Submit</button>


            </form>

          <div id="google-signin"></div>


          {isLoggedIn &&
          <div>
            <div>
              <img src={imageUrl} />
            </div>
            <div>{name}</div>
            <div>{email}</div>

          </div>
          }
        </main>
    )
};

export default Login;
